export const allEmojis = splitter.splitGraphemes(allEmojiStr);

function baseConvert(digits: number[], srcb: number, destb: number) {
	let start = 0,
		result = [];
	while (true) {
		let carry = 0,
			done = true;
		for (let i = start; i < digits.length; i++) {
			let p = srcb * carry + digits[i];
			digits[i] = Math.floor(p / destb);
			carry = p % destb;
			if (done) {
				if (!digits[i]) start = i;
				else done = false;
			}
		}
		result.unshift(carry);
		if (done) return result;
	}
}

function pad(n: string) {
	return n.length < 2 ? "0" + n : n;
}

function uintToString(uintArray: number[]) {
	let str = "";
	for (var i = 0, len = uintArray.length; i < len; ++i) {
		str += "%" + pad(uintArray[i].toString(16));
	}

	return decodeURIComponent(str);
}

export function encodji(str: string) {
	const strBuff = [...Buffer.from(str)];
	const emojiPoints = baseConvert([...strBuff], 256, allEmojis.length);
	return emojiPoints.map((x) => allEmojis[x]).join("");
}

export function decodji(str: string) {
	const emojiArray = splitter.splitGraphemes(str);
	const emojiPoints = emojiArray
		.map((x) => allEmojis.indexOf(x))
		.filter((x) => x !== -1);
	const hexPoints = baseConvert([...emojiPoints], allEmojis.length, 256);
	return uintToString(hexPoints);
}

export function removeNonEmoji(str: string) {
	return splitter
		.splitGraphemes(str)
		.filter((x) => allEmojis.indexOf(x) !== -1)
		.join("");
}
